import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoins } from '@fortawesome/free-solid-svg-icons'
import {Link} from 'react-router-dom'
import { TweenMax } from "gsap/TweenMax";


const TipButton = styled(Link)`
  background: none;
  color: #f3e367;
  border: 2px solid #f3e367;
  padding: 1.6rem 2.8rem;
  font-size: 1.9rem;
  font-family: 'Ubuntu', sans-serif;
  border-radius: 10px;
  transition: all 200ms ease-out;
  text-decoration: none;
  margin-top: 1rem;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 1px;

  :hover {
    cursor: pointer;
    background: #f3e367;
    color: black;
  }

  :focus {
      outline: none;
  }

  svg {
    margin-right: 0.8rem;
  }
`;



const Tip = ({location}) => {
    //Fancy JS Logic
    const { state = {} } = location;
    const { modal } = state;


    const buttonUp = e => {
      const btn = e.target;
      TweenMax.to(btn, 0.1, {y: -4})
    }

    const buttonDown = e => {
      const btn = e.target;
      TweenMax.to(btn, 0.1, {y: 0})
    }
    
    return (
      <div className={modal ? 'modal' : undefined}>
        <TipButton onMouseEnter={buttonUp} onMouseLeave={buttonDown} to ={{
          pathname: '/TipForm',
          state: { modal: true },
        }}><FontAwesomeIcon icon={faCoins} />Add Tip</TipButton>
        </div>
        )
    
}

export default Tip
