import React from 'react';
import { withFormik, Form, Field } from 'formik';
import * as Yup from 'yup';

import Swal from 'sweetalert2'
import styled from 'styled-components'


const H5 = styled.h5`
color:	#B22222;
`
const TipFormLabel = styled.label`
  color: #f3e367;
  font-size: 3.2rem;
  font-family: 'Ubuntu', sans-serif;
`;



function TipForm({ touched, errors }) {

  const style = {
    margin: '0 auto',
    marginTop: '120px',
    width: '800px',
    height: '500px',
    background: '#202020',
    borderRadius: '5px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '5rem',
  }

  return (
    <Form style={style}>
    <TipFormLabel htmlFor = "username">Enter tip amount</TipFormLabel>
    <Field id="payment" type="payment" autoComplete="off" placeholder="Amount" name = "payment"/>
     <H5>{touched.payment && errors.username}</H5>
    
    <button type="submit">Enter Payment :)</button>
 </Form> 
 
  );
}

export default withFormik({
  mapPropsToValues() {
    return {
       
        payment: ''
       
    };
  },
  validationSchema: Yup.object().shape({
    username: Yup.string()
    .min(1,"Must tip at least 1 dollar")
    .required("Oops please enter a payment amount"),

  }),
  handleSubmit(values, formikBag) {
    

    //   {if(token === null){
    //     Swal.fire({
    //         position: 'center',
    //         type: 'error',
    //         title: 'Try Again!',
    //         showConfirmButton: false,
    //         timer: 2500
    //       })
    //   }else{
        Swal.fire({
            position: 'center',
            type: 'success',
            title: 'Thanks for your tip',
            showConfirmButton: false,
            timer: 1500
          })
    //   }}

  }
})(TipForm);
