import React from 'react'
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
//Use react router to make the button go to a new route that renders the CreateWorker.js Component
import { TweenMax } from "gsap/TweenMax";


const WorkerButton = styled.button`
    font-size: 2rem;
    padding: 1rem;
    border-radius: 5px;
    background: #252525;
    font-family: 'Ubuntu', sans-serif;
    text-transform: uppercase;
    border: none;
    color: #f3e367;
    margin: 2.5rem;

    &:hover {
        cursor: pointer;
    }

    &:focus {
        outline: none;
    }

    svg {
        margin-right: 1rem;
    }
`;

function CreateWorkerButton() {

    const workerButtonHover = e => {
        const btn = e.target;
        TweenMax.to(btn, 0.1, {scaleX: '1.05'});    
    }

    return (
        <WorkerButton onMouseEnter={workerButtonHover} onMouseLeave={() => workerButtonHover.reverse()}><FontAwesomeIcon icon={faPlusSquare} />New Worker</WorkerButton>   
    )
}

export default CreateWorkerButton
